// package: health.rpc
// file: health/rpc/rpc.proto

import * as health_rpc_rpc_pb from "../../health/rpc/rpc_pb";
import {grpc} from "@improbable-eng/grpc-web";

type RPCServiceCheck = {
  readonly methodName: string;
  readonly service: typeof RPCService;
  readonly requestStream: false;
  readonly responseStream: false;
  readonly requestType: typeof health_rpc_rpc_pb.CheckRequest;
  readonly responseType: typeof health_rpc_rpc_pb.CheckResponse;
};

export class RPCService {
  static readonly serviceName: string;
  static readonly Check: RPCServiceCheck;
}

export type ServiceError = { message: string, code: number; metadata: grpc.Metadata }
export type Status = { details: string, code: number; metadata: grpc.Metadata }

interface UnaryResponse {
  cancel(): void;
}
interface ResponseStream<T> {
  cancel(): void;
  on(type: 'data', handler: (message: T) => void): ResponseStream<T>;
  on(type: 'end', handler: (status?: Status) => void): ResponseStream<T>;
  on(type: 'status', handler: (status: Status) => void): ResponseStream<T>;
}
interface RequestStream<T> {
  write(message: T): RequestStream<T>;
  end(): void;
  cancel(): void;
  on(type: 'end', handler: (status?: Status) => void): RequestStream<T>;
  on(type: 'status', handler: (status: Status) => void): RequestStream<T>;
}
interface BidirectionalStream<ReqT, ResT> {
  write(message: ReqT): BidirectionalStream<ReqT, ResT>;
  end(): void;
  cancel(): void;
  on(type: 'data', handler: (message: ResT) => void): BidirectionalStream<ReqT, ResT>;
  on(type: 'end', handler: (status?: Status) => void): BidirectionalStream<ReqT, ResT>;
  on(type: 'status', handler: (status: Status) => void): BidirectionalStream<ReqT, ResT>;
}

export class RPCServiceClient {
  readonly serviceHost: string;

  constructor(serviceHost: string, options?: grpc.RpcOptions);
  check(
    requestMessage: health_rpc_rpc_pb.CheckRequest,
    metadata: grpc.Metadata,
    callback: (error: ServiceError|null, responseMessage: health_rpc_rpc_pb.CheckResponse|null) => void
  ): UnaryResponse;
  check(
    requestMessage: health_rpc_rpc_pb.CheckRequest,
    callback: (error: ServiceError|null, responseMessage: health_rpc_rpc_pb.CheckResponse|null) => void
  ): UnaryResponse;
}

